import React from 'react';
import PopupWithForm from './PopupWithForm.js';
import {CurrentUserContext} from '../contexts/CurrentUserContext.js'

function EditProfilePopup({isOpen, onClose, onUpdateUser, isRender}) {
  const currentUser = React.useContext(CurrentUserContext);

  const [name, setName] = React.useState('');
  const [description, setDescription] = React.useState('');

  React.useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [currentUser]);

  function handleChangeName(evt) {
    setName(evt.target.value);
  }

  function handleChangeDescription(evt) {
    setDescription(evt.target.value);
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    onUpdateUser({
      name,
      about: description,
    })
  }


    return (
        <PopupWithForm
        title='Редактировать профиль'
        name='edit-profile'
        btnName='Сохранить'
        isOpen={isOpen}
        onClose={onClose}
        onSubmit={handleSubmit}>
          <input value={name || ''} onCange={handleChangeName} className="popup__input popup__input_type_profile-name" type="text" id="name-input" name="name" placeholder="Введите имя:" minLength="2" maxLength="40" required />
          <span id="name-input-error" className="popup__input-error"></span>
