//ФУНКЦИОНАЛЬНЫЙ КОМПОНЕНТ КАРТОЧКИ
import React from 'react';
import {CurrentUserContext} from '../contexts/CurrentUserContext.js'

function Card({name, card, link, count, onCardClick, onCardDelete}) {
    const currentUser = React.useContext({CurrentUserContext});

  // Определяем, являемся ли мы владельцем текущей карточки
  const isOwn = card.owner._id === currentUser._id;

  // Создаём переменную, которую после зададим в `className` для кнопки удаления
  const cardDeleteButtonClassName = (
    `card__btn-trash ${isOwn ? 'card__btn-trash_visible' : 'card__btn-trash_hidden'}`
  );

  function handleCardDelete() {
    onCardDelete(card);
  }

  // Определяем, есть ли у карточки лайк, поставленный текущим пользователем
  const isLiked = card.likes.some(i => i._id === currentUser._id);

  // Создаём переменную, которую после зададим в `className` для кнопки лайка
  const cardLikeButtonClassName = `...`;

    function handleCardClick() {
        onCardClick(card);
    }

    return (
        <li className="gallery__item card">
          <button type="button" className={cardDeleteButtonClassName} onClick={handleCardDelete}></button>
          <img className="card__image" src={link} alt={`Фото ${name}`} onClick={handleCardClick}/>
          <div className="card__wrapper">
            <h3 className="card__title">{name}</h3>
            <div className="card__like-area">
            <button type="button" className="card__btn-like"></button>
            <p className="card__sum-like">{count}</p>
          </div>
          </div>
        </li>
    );
}

export default Card;
