import React from 'react';
import api from '../utils/api.js';
import Card from './Card.js';
import {CurrentUserContext} from '../contexts/CurrentUserContext.js';

function Main(props) {
//ИСПОЛЬЗУЕМ ХУКИ СОСТОЯНИЯ 
    const currentUser = React.useContext(CurrentUserContext);

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
//ХУК ЭФФЕКТА ДЛЯ ПОЛУЧЕНИЯ КАРТОЧЕК И ДАННЫХ ПОЛЬЗОВАТЕЛЯ
    // React.useEffect(() => {
    //     Promise.all([api.getUserData(), api.getInitialCards()])
    //     .then(([userData, cardsData]) => {
    //         setUserName(userData.name);
    //         setUserDescription(userData.about);
    //         setUserAvatar(userData.avatar);
    //         setCards(cardsData);
    //     })
    //     .catch((err) => {
    //         console.log(err);
    //     })
    // }, []);

    return (
        <div className="main">
                <section className="profile">
                    <img src={currentUser.avatar} alt="Аватар пользователя" className="profile__avatar" />
                    <button className="profile__change-btn" type="button" onClick={props.onEditAvatar}></button>
                <div className="profile__content">
                    <div className="profile__wrapper">
                        <h1 className="profile__name">{currentUser.name}</h1>
                        <button type="button" className="profile__button-edit" onClick={props.onEditProfile}></button>
                    </div>
                    <p className="profile__job">{currentUser.about}</p>
                </div>
                    <button type="button" className="profile__button-add" onClick={props.onAddPhoto}></button>
                </section>

                <section className="gallery">
                    <ul className="gallery__list">
                        {/* МАПИМ КАРТОЧКИ */}
                        {props.cards.map((item) => {
                            return (
                                <Card
                                 key={item._id}
                                 card={item}
                                 name={item.name}
                                 link={item.link}
                                 count={item.likes.length}
                                 onCardClick={props.onCardClick}
                                 ></Card>
                            )
                        })}
                    </ul>
                </section>
        </div>
    );
}

export default Main;
