//ФУНКЦИОНАЛЬНЫЙ КОМПОНЕНТ ДЛЯ ПОПАПА С ФОРМОЙ
import React from 'react';

function PopupWithForm({name, title, isOpen, onClose, children, btnName, onSubmit}) {

  React.useEffect(() => {

    if(!isOpen) return;
// ЗАКРЫТИЕ ПОПАПА ПО КНОПКЕ ESC
    const handleEscClose = (event) => {
      if (event.key === 'Escape') {
        onClose();
      }
    };
// ЗАКРЫТИЕ ПОПАПА ПО КЛИКЕ ВНЕ ОБЛАСТИ
    const handlePopupClick = (event) => {
      if (event.target.classList.contains('popup')) {
        onClose();
      }
    }
//ВЕШАЕМ СЛУШАТЕЛИ 
    document.addEventListener('keydown', handleEscClose);
    document.addEventListener('mousedown', handlePopupClick);
//УДАЛЯЕМ СЛУШАТЕЛИ
    return () => {
      document.removeEventListener('keydown', handleEscClose);
      document.removeEventListener('mousedown', handlePopupClick);
    };

  }, [isOpen, onClose]);

  return (
    <div>
      <div className={`popup popup_type_${name}} ${isOpen ? 'popup_opened' : false }`} id={`popup-${name}`}>
          <div className="popup__container">
            <button onClick={onClose} type="button" className="popup__btn-close"></button>
            <h3 className="popup__title">{title}</h3>
            <form className="popup__form" name={`${name}-form`} noValidate>
              {children}
              <button type="submit" name="button" className="popup__btn-save" on>{btnName}</button>
            </form>
          </div>
      </div>
    </div>
  )
}

export default PopupWithForm;
