import React from 'react';
import PopupWithForm from './PopupWithForm';

function EditAvatarPopup({isOpen, onClose, onUpdateAvatar, isRender}) {

    const avatarRef = React.useRef();

    function handleSubmit(evt) {
        evt.preventDefault();
        onUpdateAvatar(avatarRef.current)
    }

    return (
        <PopupWithForm
        name='change-avatar'
        title='Обновить аватар'
        isOpen={isOpen}
        btnName='Сохранить'
        onClose={onClose}>
            <input className="popup__input popup__input_type_change-avatar-link" type="url" id="avatar-input" name="link" placeholder="Ссылка на картинку" minLength="2" maxLength="200" required />
            <span id="avatar-input-error" className="popup__input-error"></span>
        </PopupWithForm>
    );
}

export default EditAvatarPopup;
