import React from 'react';
import api from '../utils/api.js';

function Main(props) {

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUseAvatar] = React.useState('');

    React.useEffect(() => {
        Promise.all([api.getUserData()])
        .then(([userData]) => {
            setUserName(userData.name);
            setUserDescription(userData.about);
            setUserAvatar(userData.avatar);
        })
        .catch((err) => {
            console.log(err);
        })
    }, []);

    return (
        <div className="main">
                <section className="profile">
                    <img src={currentUser.avatar} alt="Аватар пользователя" className="profile__avatar" />
                    <button className="profile__change-btn" type="button" onClick={props.onEditAvatar}></button>
                <div className="profile__content">
                    <div className="profile__wrapper">
                        <h1 className="profile__name">{currentUser.name}</h1>
                        <button type="button" className="profile__button-edit" onClick={props.onEditProfile}></button>
                    </div>
                    <p className="profile__job">{currentUser.about}</p>
                </div>
                    <button type="button" className="profile__button-add" onClick={props.onAddPhoto}></button>
                </section>

                <section className="gallery">
                    <ul className="gallery__list">
                    </ul>
                </section>
        </div>
    );
}

export default Main;
