import React from 'react';

const useFormWithValidation = () => {
    const [values, setValues] = React.useState({});
    const [errors, setErrors] = React.useState({name: false, about: false});
    const [errorMessages, setErrorMessages] = React.useState({name: '', about: ''});
    const [isFormNotValid, setIsFormNotValid] = React.useState(false);

    React.useEffect(() => {
        setIsFormNotValid(isErrors.name || isErrors.about);
    }, [isErrors.name, isErrors.about]);

    const onChange = (evt) => {
        setValues((values) => ({...values, [evt.target.name]: evt.target.value}));
        setIsErrors((isErrors) => ({...isErrors, [evt.target.name]: evt.validity.value}));
        if(!evt.target.validity.value) {
            setErrorMessages({...errorMessages, [evt.target.name]: evt.target.validationMessage});
        } else {
            setErrorMessages({...errorMessages, [evt.target.name]: ''});
        }
    };

    return {
        values,
        isErrors,
        errorMessages,
        isFormNotValid,
        setValues,
        onChange,
    }
};

export default useFormWithValidation;
