import React from 'react';
import api from '../utils/api.js';
import Card from './Card.js';

function Main(props) {

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    const [cards,setCards] = React.useState([]);

    React.useEffect(() => {
        Promise.all([api.getUserData(), api.getInitialCards()])
        .then(([userData, cardsData]) => {
            setUserName(userData.name);
            setUserDescription(userData.about);
            setUserAvatar(userData.avatar);
            setCards(cardsData);
        })
        .catch((err) => {
            console.log(err);
        })
    }, []);

    return (
        <div className="main">
                <section className="profile">
                    <img src={userAvatar} alt="Аватар пользователя" className="profile__avatar" />
                    <button className="profile__change-btn" type="button" onClick={props.onEditAvatar}></button>
                <div className="profile__content">
                    <div className="profile__wrapper">
                        <h1 className="profile__name">{userName}</h1>
                        <button type="button" className="profile__button-edit" onClick={props.onEditProfile}></button>
                    </div>
                    <p className="profile__job">{userDescription}</p>
                </div>
                    <button type="button" className="profile__button-add" onClick={props.onAddPhoto}></button>
                </section>

                <section className="gallery">
                    <ul className="gallery__list">
                        {cards.map((card) => {
                            return (
                                <Card
                                 key={card._id}
                                 card={card}
                                 name={card.name}
                                 link={card.link}
                                 count={card.likes.length}
                                 onCardClick={props.onCardClick}
                                 ></Card>
                            )
                        })}
                    </ul>
                </section>
        </div>
    );
}

export default Main;
