    <div className="page">
      <div className="container">

        <Header></Header>

        <Main
        onAddPhoto={handleAddPhotoClick}
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        ></Main>

        <Footer></Footer>

        <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateAvatar}
        ></EditAvatarPopup>

        <EditProfilePopup
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateUser}
        ></EditProfilePopup>

        <AddPhotoPopup
        isOpen={isAddPhotoPopupOpen}
        onClose={closeAllPopups}
        ></AddPhotoPopup>

      </div>

      <template id="card" className="card-template">
      <li className="gallery__item card">
        <button type="button" className="card__btn-trash"></button>
        <img className="card__image" src="#" alt="Фото пользователя" />
        <div className="card__wrapper">
          <h3 className="card__title"></h3>
          <div className="card__like-area">
          <button type="button" className="card__btn-like"></button>
          <p className="card__sum-like"></p>
        </div>
        </div>
      </li>
      </template>
    </div>
  );
}

export default App;
