import React from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext } from '../utils/CurrentUserContext';

function EditProfilePopup({isOpen, onClose, onUpdateUser}) {

    const [name, setName] = React.useState('');
    const [description, setDescription] = React.useState('');
    const currentUser = React.useContext(CurrentUserContext);

    function handleChangeName(event) {
        setName(event.target.value);
    }

    function handleChangeDescription(event) {
        setDescription(event.target.value);
    }

    function handleSubmit(event) {
        event.preventDefault();
        onUpdateUser({
            name,
            about: description,
        })
    };

    React.useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about)
    }, [currentUser]);

    return (
        <PopupWithForm
        title='Редактировать профиль'
        name='edit-profile'
        btnName='Сохранить'
        isOpen={isOpen}
        onClose={onClose}
        onSubmit={handleSubmit}>
          <input onChange={handleChangeName} value={name || ''} className="popup__input popup__input_type_profile-name" type="text" id="name-input" name="name" placeholder="Введите имя:" minLength="2" maxLength="40" required />
          <span id="name-input-error" className="popup__input-error"></span>
          <input onChange={handleChangeDescription} value={description || ''}  className="popup__input popup__input_type_profile-job" type="text" id="job-input" name="about" placeholder="Ваша профессия:" minLength="2" maxLength="200" required />
          <span id="job-input-error" className="popup__input-error"></span>
        </PopupWithForm>
    );
}

export default EditProfilePopup;
