import React from 'react';

function PopupWithForm({name, title, isOpen, onClose, children, onSubmit, btnName}) {

  React.useEffect(() => {

    if(!isOpen) return;

    const handleEscClose = (event) => {
      if (event.key === 'Escape') {
        onClose();
      }
    };

    document.addEventListener('keydown', handleEscClose);

    return () => {
      document.removeEventListener('keydown', handleEscClose);
    };

  }, [isOpen, onClose]);

  return (
    <div>
      <div className={`popup popup_type_${name}} ${isOpen ? 'popup_opened' : false }`} id={`popup-${name}`}>
          <div className="popup__container">
            <button onClick={onClose} type="button" className="popup__btn-close"></button>
            <h3 className="popup__title">{title}</h3>
            <form className="popup__form" name={`${name}-form`} onSubmit={onSubmit} noValidate>
              <>{children}</>
              <button type="submit" name="button" className="popup__btn-save">{btnName}</button>
            </form>
          </div>
      </div>
    </div>
  )
}

export default PopupWithForm;
