import React from 'react';
import PopupWithForm from './PopupWithForm';
import useFormWithValidation from '../utils/hooks/useFormWithValidation';

function EditAvatarPopup({isOpen, onClose, onUpdateAvatar, isRender}) {


    const avatarRef = React.useRef();

    function handleSubmit(evt) {
        evt.preventDefault();
        onUpdateAvatar(avatarRef.current.value);
    }

    React.useEffect(() => {
        avatarRef.current.value ='';
    }, [isOpen]);

    return (
        <PopupWithForm
        name='change-avatar'
        title='Обновить аватар'
        isOpen={isOpen}
        btnName={isRender ? 'Сохранение...' : 'Сохранить'}
        onClose={onClose}
        onSubmit={handleSubmit}>
            <input ref={avatarRef} className="popup__input popup__input_type_change-avatar-link" type="url" id="avatar-input" name="link" placeholder="Ссылка на картинку" minLength="2" maxLength="200" required />
            <span id="avatar-input-error" className={`popup__input-error ${!isValid.about ? 'popup__input-error_active' : ''} `}></span>
        </PopupWithForm>
    );
}

export default EditAvatarPopup;
