import React from "react";
import { Switch, Route, Redirect, useHistory } from "react-router-dom";
import ProtectedRoute from "./ProtectedRoute";

import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import EditAvatarPopup from "./EditAvatarPopup";
import EditProfilePopup from "./EditProfilePopup";
import AddPhotoPopup from "./AddPhotoPopup";
import ImagePopup from "./ImagePopup";
import ConfirmPopup from "./ConfirmPopup";
import Register from "./Register";
import Login from "./Login"
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";
import api from "../utils/api.js";

function App() {
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);

  const [isEditProfilePopupOpen, setEditProfileClick] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarClick] = React.useState(false);
  const [isAddPhotoPopupOpen, setAddPhotoClick] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});

  const [selectedCardDeleteConfirm, setSelectedCardDeleteConfirm] =
    React.useState({ isOpen: false, card: {} });

  const [renderSave, setRenderSave] = React.useState(false);

  const [loggedIn, setLoggedIn] = React.useState(false);

  //Выводим информацию профиля
  React.useEffect(() => {
    api
      .getUserData()
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  //Выводим карточки
  React.useEffect(() => {
    api
      .getInitialCards()
      .then((data) => {
        setCards(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  //ОБРАБОТЧИКИ ПОПАПОВ
  function handleEditAvatarClick() {
    setEditAvatarClick(true);
  }

  function handleEditProfileClick() {
    setEditProfileClick(true);
  }

  function handleAddPhotoClick() {
    setAddPhotoClick(true);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleCardDeleteClick(card) {
    setSelectedCardDeleteConfirm({
      ...selectedCardDeleteConfirm,
      isOpen: true,
      card: card,
    });
  }

  function handleCardDelete(card) {
    setRenderSave(true);
    api
      .deleteCard(card._id)
      .then(() => {
        const newCards = cards.filter((c) =>
          c._id === card._id ? false : true
        );
        setCards(newCards);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setRenderSave(false);
      });
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    api
      .changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        const newCards = cards.map((c) => (c._id === card._id ? newCard : c));
        setCards(newCards);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  function handleUpdateUser(newUserData) {
    setRenderSave(true);
    api
      .saveUserChanges(newUserData)
      .then((data) => {
        setCurrentUser(data);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setRenderSave(false);
      });
  }

  function handleUpdateAvatar(newAvatarLink) {
    setRenderSave(true);
    api
      .changeAvatar(newAvatarLink)
      .then((data) => {
        setCurrentUser({ ...currentUser, avatar: data.avatar });
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setRenderSave(false);
      });
  }

  function handleAddPhotoSubmit(cardData) {
    setRenderSave(true);
    api
      .postNewCard(cardData)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setRenderSave(false);
      });
  }

  function handleRegister(){
    console.log('register')
  }
  function handleLogin(){
    console.log('login')
  }

  function closeAllPopups() {
    setAddPhotoClick(false);
    setEditAvatarClick(false);
    setEditProfileClick(false);
    setSelectedCard({});
    setSelectedCardDeleteConfirm({
      ...selectedCardDeleteConfirm,
      isOpen: false,
    });
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <div className="container">

          <Header />

          <Switch>
            <ProtectedRoute
            exact
            path='/'
            loggedIn={loggedIn}
            component={Main}
            cards={cards}
            onAddPhoto={handleAddPhotoClick}
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onCardClick={handleCardClick}
            onLikeCard={handleCardLike}
            onDeleteCard={handleCardDeleteClick}
            />

            <Route path='/sign-up'>
              <Register onRegister={handleRegister} />
            </Route>

            <Route path='/sign-in'>
              <Login onLo={handleLogin} />
            </Route>

            <Route>
              {loggedIn ? (
                <Redirect to="/" />
              ) : (
                <Redirect to="/sign-in" />
              )}
            </Route>

          </Switch>
          {/* <Main
            cards={cards}
            onAddPhoto={handleAddPhotoClick}
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onCardClick={handleCardClick}
            onLikeCard={handleCardLike}
            onDeleteCard={handleCardDeleteClick}
          /> */}
          <Footer />
          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
            isRender={renderSave}
          />
          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
            isRender={renderSave}
          />
          <AddPhotoPopup
            isOpen={isAddPhotoPopupOpen}
            onClose={closeAllPopups}
            onAddPhoto={handleAddPhotoSubmit}
            isRender={renderSave}
          />
          <ImagePopup
            onClose={closeAllPopups}
            card={selectedCard}
            isRender={renderSave}
          />
          <ConfirmPopup
            deleteCard={selectedCardDeleteConfirm}
            onClose={closeAllPopups}
            onCardDelete={handleCardDelete}
            isRender={renderSave}
          />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
