import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import EditAvatarPopup from "./EditAvatarPopup";
import EditProfilePopup from "./EditProfilePopup";
import AddPhotoPopup from "./AddPhotoPopup";
import ImagePopup from "./ImagePopup";
import ConfirmPopup from "./ConfirmPopup";
import {CurrentUserContext} from '../contexts/CurrentUserContext.js';
import api from '../utils/api.js'


function App() {
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);

  const [isEditProfilePopupOpen, setEditProfileClick] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarClick] = React.useState(false);
  const [isAddPhotoPopupOpen, setAddPhotoClick] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});

  const [selectedCardDeleteConfirm, setSelectedCardDeleteConfirm] = React.useState({isOpen:false, card:{}});

  const [renderSave, setRenderSave] = React.useState(false);


//Выводим информацию профиля
React.useEffect(() => {
  api.getUserData()
  .then(data => {
    setCurrentUser(data);
  })
  .catch((err) => {
    console.log(err)
  })
},[]);

//Выводим карточки
React.useEffect(() => {
  api.getInitialCards()
  .then(data => {
    setCards(data)
  })
  .catch((err) => {
    console.log(err);
  })
}, [])


//ОБРАБОТЧИКИ ПОПАПОВ
function handleEditAvatarClick() {
    setEditAvatarClick(true);
}

function handleEditProfileClick() {
    setEditProfileClick(true);
}

function handleAddPhotoClick() {
    setAddPhotoClick(true);
}

function handleCardClick(card){
  setSelectedCard(card);
}

function handleCardDeleteClick(card) {
  setSelectedCardDeleteConfirm({...selectedCardDeleteConfirm, isOpen:true, card:card})
}

function handleCardDelete(card) {
  setRenderSave(true);
  api.deleteCard(card._)
  .then((data) => {
    const newCards = cards.filter((c) => c._id === card._id ? false : true);
    setCards(newCards);
    closeAllPopups();
  })
  .catch((err) => {
    console.log(err)
  })
  .finally(() => {
    setRenderSave(false);
  })
}

function closeAllPopups() {
  setAddPhotoClick(false);
  setEditAvatarClick(false);
  setEditProfileClick(false);
  setSelectedCard({});
}


  return (
    <CurrentUserContext.Provider value={currentUser}>

      <div className="page">
        <div className="container">

          <Header/>

          <Main
          cards={cards}
          onAddPhoto={handleAddPhotoClick}
          onEditAvatar={handleEditAvatarClick}
          onEditProfile={handleEditProfileClick}
          onCardClick={handleCardClick}
          onDeleteCard={handleCardDeleteClick}
          />

          <Footer/>

          <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          />

          <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          />

          <AddPhotoPopup
          isOpen={isAddPhotoPopupOpen}
          onClose={closeAllPopups}
          />

          <ImagePopup
          onClose={closeAllPopups}
          card={selectedCard}
          />

          <ConfirmPopup
          deleteCard={selectedCardDeleteConfirm}
          onClose={closeAllPopups}
          onCardDelete={handleCardDelete}
          />

        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
