import React from 'react';
import PopupWithForm from './PopupWithForm';

function AddPhotoPopup({isOpen, onClose, onAddPhoto, isRender}) {
    const [name, setName] = React.useState('');
    const [link,setLink] = React.useState('');

    function handleAddName(evt) {
      setName(evt.target.value);
    }

    function handleAddLink(evt) {
      setLink(evt.target.value);
    }

    function handleSubmit(evt) {
      evt.preventDefault();
      onAddPhoto({name, link});
    }

    React.useEffect(() => {
      setName('');
      setLink('');
    }, [isOpen])
    return (
        <PopupWithForm
        title = 'Новое место'
        name = 'add-photo'
        btnName='Создать'
        isOpen={isOpen}
        onClose={onClose}
        onSubmit={handleSubmit}>
          <input value={name} onChange={handleAddName} className="popup__input popup__input_type_card-title" type="text" id="title-input" name="name" placeholder="Название" minLength="2" maxLength="40" required />
          <span id="title-input-error" className="popup__input-error"></span>
          <input value={link} on className="popup__input popup__input_type_card-link" type="url" id="link-input" name="link" placeholder="Ссылка на картинку" minLength="2" maxLength="200" required />
          <span id="link-input-error" className="popup__input-error"></span>
        </PopupWithForm>
    );
}

export default AddPhotoPopup;
