import React from 'react';

function Card({name, card, link, count, onCardClick}) {

    function handleCardClick() {
        onCardClick(card);
    }

    return (
        <>
        <div id="card" className="card-template">
        <li className="gallery__item card">
          <button type="button" className="card__btn-trash"></button>
          <img className="card__image" src={link} alt={`Фото ${name}`} onClick={handleCardClick}/>
          <div className="card__wrapper">
            <h3 className="card__title">{name}</h3>
            <div className="card__like-area">
            <button type="button" className="card__btn-like"></button>
            <p className="card__sum-like">{count}</p>
          </div>
          </div>
        </li>
      </div>
      </>
    );
}

export default Card;
