import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import EditAvatarPopup from "./EditAvatarPopup";
import EditProfilePopup from "./EditProfilePopup";
import { CurrentUserContext } from "../utils/CurrentUserContext";
import ConfirmPopup from "./ConfirmPopup";
import AddPhotoPopup from "./AddPhotoPopup";
import api from "../utils/api.js";


function App() {
  const [isEditProfilePopupOpen, setEditProfileClick] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarClick] = React.useState(false);
  const [isAddPhotoPopupOpen, setAddPhotoClick] = React.useState(false);
  const [currentUser, setCurrentUser] = React.useState({});

  React.useEffect(() => {
    api.getUserData()
    .then(data => {
      setCurrentUser(data);
    })
    .catch(err => {
      console.log(err);
    })
  }, []);

  function handleEditAvatarClick() {
    setEditAvatarClick(true);
}

function handleEditProfileClick() {
    setEditProfileClick(true);
}

function handleAddPhotoClick() {
    setAddPhotoClick(true);
}

function closeAllPopups() {
  setAddPhotoClick(false);
  setEditAvatarClick(false);
  setEditProfileClick(false);
}

function handleUpdateAvatar({avatar}) {
  api.changeAvatar(avatar)
  .then((data) => {
    setCurrentUser(data);
    closeAllPopups();
  })
  .catch(err => {
    console.log(err);
  })
}

function handleUpdateUser(newUserData){
  console.log('User')
}

  return (
    <div className="page">
      <div className="container">

        <Header></Header>

        <Main
        onAddPhoto={handleAddPhotoClick}
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        ></Main>

        <Footer></Footer>

        <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateAvatar}
        ></EditAvatarPopup>

        <EditProfilePopup
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateUser}
        ></EditProfilePopup>

        <AddPhotoPopup
        isOpen={isAddPhotoPopupOpen}
        onClose={closeAllPopups}
        ></AddPhotoPopup>

      </div>

      <div className="popup popup_type_edit-profile" id="popup-edit-profile">
      <div className="popup__container">
        <button type="button" className="popup__btn-close"></button>
        <h3 className="popup__title">Редактировать профиль</h3>
        <form className="popup__form" action="index.html" name="edit-form" method="post" noValidate>
          <input className="popup__input popup__input_type_profile-name" type="text" id="name-input" name="name" placeholder="Введите имя:" minLength="2" maxLength="40" required />
          <span id="name-input-error" className="popup__input-error"></span>
          <input className="popup__input popup__input_type_profile-job" type="text" id="job-input" name="about" placeholder="Ваша профессия:" minLength="2" maxLength="200" required />
          <span id="job-input-error" className="popup__input-error"></span>
          <button type="submit" name="button" className="popup__btn-save">Сохранить</button>
        </form>
      </div>
      </div>

      <div className="popup popup_type_change-avatar" id="popup-change-avatar">
      <div className="popup__container">
        <button type="button" className="popup__btn-close"></button>
        <h3 className="popup__title">Обновить аватар</h3>
        <form className="popup__form" action="index.html" name="change-avatar-form" method="post" noValidate>
          <input className="popup__input popup__input_type_change-avatar-link" type="url" id="avatar-input" name="link" placeholder="Ссылка на картинку" minLength="2" maxLength="200" required />
          <span id="avatar-input-error" className="popup__input-error"></span>
          <button type="submit" name="button" className="popup__btn-save">Сохранить</button>
        </form>
      </div>
      </div>

      <div className="popup popup_type_add-photo" id="popup-add-photo">
      <div className="popup__container">
        <button type="button" className="popup__btn-close"></button>
        <h3 className="popup__title">Новое место</h3>
        <form className="popup__form" action="index.html" name="add-form" method="post" noValidate>
          <input className="popup__input popup__input_type_card-title" type="text" id="title-input" name="name" placeholder="Название" minLength="2" maxLength="40" required />
          <span id="title-input-error" className="popup__input-error"></span>
          <input className="popup__input popup__input_type_card-link" type="url" id="link-input" name="link" placeholder="Ссылка на картинку" minLength="2" maxLength="200" required />
          <span id="link-input-error" className="popup__input-error"></span>
          <button type="submit" name="button" className="popup__btn-save">Создать</button>
        </form>
      </div>
      </div>

      <div className="popup popup_photo" id="popup-show-photo">
        <div className="popup__container popup__container_photo">
        <button type="button" className="popup__btn-close"></button>
        <img className="popup__photo" src="#" alt="#"/>
        <h2 className="popup__photo-title"></h2>
        </div>
      </div>

      <div className="popup popup_type_confirm" id="popup-confirm">
      <div className="popup__container">
      <button type="button" className="popup__btn-close"></button>
      <h3 className="popup__title popup__title_type_confirm">Вы уверены?</h3>
      <form className="popup__form" action="index.html" name="confirm-form" method="post" noValidate>
        <button type="submit" name="button" className="popup__btn-save">Да</button>
      </form>
      </div>
      </div>

      <template id="card" className="card-template">
      <li className="gallery__item card">
        <button type="button" className="card__btn-trash"></button>
        <img className="card__image" src="#" alt="Фото пользователя" />
        <div className="card__wrapper">
          <h3 className="card__title"></h3>
          <div className="card__like-area">
          <button type="button" className="card__btn-like"></button>
          <p className="card__sum-like"></p>
        </div>
