import React from 'react';


function ImagePopup({onClose, card}) {

    React.useEffect(() => {

        const handleEscClose = (event) => {
            if (event.key === 'Escape') {
              onClose();
            }
          };

        if(card.name) {
            document.addEventListener('keydown', handleEscClose);
        }

        return () => document.removeEventListener('keydown', handleEscClose);
    }, [card]);

    return (
        <div className={`popup popup_photo  ${card.name ? 'popup_opened' : ''}`} id="popup-show-photo">
            <div className="popup__container popup__container_photo">
                <button type="button" className="popup__btn-close" onClick={onClose}></button>
                <img className="popup__photo" src={card.link} alt={card.name}/>
                <h2 className="popup__photo-title">{card.name}</h2>
            </div>
        </div>
    );
}

export default ImagePopup;
